mapper.insert:
INSERT INTO def_document (
    id, docName, docSize, docType,
    created_by, updated_by, created_date, updated_date, deleted_flag
) VALUES (
             #{id}, #{docName}, #{docSize}, #{docType},
             #{createdBy}, #{updatedBy}, #{createdDate}, #{updatedDate}, #{deletedFlag}
         )

INSERT INTO def_document (
id, docName, docSize, docType,
created_by, updated_by, created_date, updated_date, deleted_flag
) VALUES

    (
    #{item.id}, #{item.docName}, #{item.docSize}, #{item.docType},
    #{item.createdBy}, #{item.updatedBy}, #{item.createdDate}, #{item.updatedDate}, #{item.deletedFlag}
    )

mapper.update:
UPDATE def_document
docName = #{docName}, docSize = #{docSize}, docType = #{docType}, created_by = #{createdBy}, updated_by = #{updatedBy}, created_date = #{createdDate}, updated_date = #{updatedDate}, deleted_flag = #{deletedFlag},
WHERE id = #{id}

mapper.select:
SELECT

FROM def_document

    AND id = #{id}
    AND docName LIKE CONCAT('%', #{docName}, '%')
    AND docSize = #{docSize}
    AND docType = #{docType}
    AND created_by = #{createdBy}
    AND updated_by = #{updatedBy}
    AND created_date = #{createdDate}
    AND updated_date = #{updatedDate}
    AND deleted_flag = #{deletedFlag}

SELECT

FROM def_document

    AND id = #{condition.id}
    AND docName LIKE CONCAT('%', #{condition.docName}, '%')
    AND docSize = #{condition.docSize}
    AND docType = #{condition.docType}
    AND created_by = #{condition.createdBy}
    AND updated_by = #{condition.updatedBy}
    AND created_date = #{condition.createdDate}
    AND updated_date = #{condition.updatedDate}
    AND deleted_flag = #{condition.deletedFlag}

LIMIT #{pageVO.offset}, #{pageVO.pageSize}

SELECT COUNT(*) FROM def_document

    AND id = #{id}
    AND docName LIKE CONCAT('%', #{docName}, '%')
    AND docSize = #{docSize}
    AND docType = #{docType}
    AND created_by = #{createdBy}
    AND updated_by = #{updatedBy}
    AND created_date = #{createdDate}
    AND updated_date = #{updatedDate}
    AND deleted_flag = #{deletedFlag}
